import React , {useState} from "react";
import "./AddNewStudent.css"

const AddNewStudent = (props) => {
    let tempStudent_Name;
    let tempStudent_Number;
    let tempStudent_User;
    let tempStudent_Pass;

    const Name_changeHandler = (event) => {
        tempStudent_Name = event.target.value
    }
    const SN_changeHandler = (event) => {
        tempStudent_Number = event.target.value
    }
    const USER_changeHandler = (event) => {
        tempStudent_User = event.target.value
    }
    const PASS_changeHandler = (event) => {
        tempStudent_Pass = event.target.value
    }

    const SubmitHandler = (event) => {
        event.preventDefault();
        tempStudent_Name = '';
        tempStudent_Number = '';
        tempStudent_User = '';
        tempStudent_Pass = '';
    }

    return (
        <div>
            <form onSubmit={SubmitHandler} className="add_student__form">
                <input onChange={Name_changeHandler} value={tempStudent_Name} className="input_student" type="text" placeholder="Enter Name Of Student" />
                <input onChange={SN_changeHandler} value={tempStudent_Number} className="input_student" type="number" placeholder="Enter Student Number" />
                <input onChange={USER_changeHandler} value={tempStudent_User} className="input_student" type="text" placeholder="Enter Student userName" />
                <input onChange={PASS_changeHandler} value={tempStudent_Pass} className="input_student" type="text" placeholder="Enter Student pass" />
                <button onClick={SubmitHandler} type="submit">Add this Student</button>
            </form>
        </div>
    );
}

export default AddNewStudent;
