import React, { useState } from "react";
import "./AddNewStudent.css";

const AddNewStudent = (props) => {
    // Declare state variables for each input field
    const [studentName, setStudentName] = useState("");
    const [studentNumber, setStudentNumber] = useState("");
    const [studentUser, setStudentUser] = useState("");
    const [studentPass, setStudentPass] = useState("");

    // Handle input changes
    const Name_changeHandler = (event) => {
        setStudentName(event.target.value);
    };
    const SN_changeHandler = (event) => {
        setStudentNumber(event.target.value);
    };
    const USER_changeHandler = (event) => {
        setStudentUser(event.target.value);
    };
    const PASS_changeHandler = (event) => {
        setStudentPass(event.target.value);
    };

    // Handle form submission and reset the fields
    const SubmitHandler = (event) => {
        event.preventDefault();
        // Reset the state values to clear the inputs
        setStudentName("");
        setStudentNumber("");
        setStudentUser("");
        setStudentPass("");
    };

    return (
        <div>
            <form onSubmit={SubmitHandler} className="add_student__form">
                <input
                    onChange={Name_changeHandler}
                    value={studentName}
                    className="input_student"
                    type="text"
                    placeholder="Enter Name Of Student"
                />
                <input
                    onChange={SN_changeHandler}
                    value={studentNumber}
                    className="input_student"
                    type="number"
                    placeholder="Enter Student Number"
                />
                <input
                    onChange={USER_changeHandler}
                    value={studentUser}
                    className="input_student"
                    type="text"
                    placeholder="Enter Student userName"
                />
                <input
                    onChange={PASS_changeHandler}
                    value={studentPass}
                    className="input_student"
                    type="text"
                    placeholder="Enter Student pass"
                />
                <button type="submit">Add this Student</button>
            </form>
        </div>
    );
};

export default AddNewStudent;
