    setStudent(student.splice(indexWhichShouldBeDeleted, 1));
  }

  return (
    <div className='container'>
      {
        people.map(student => (
          <StudentCard
            onChange={StudentCardDeleteListener}
            nameK={student.name}
            passK={student.pass}
            numberK={student.studentNum}
            id={student.studentNum}
            key={student.studentNum}
          />
        ))
      }
    </div>
  );
}

export default App;
