import './App.css';
import StudentCard from './StudentCards';
import React, { useState } from 'react';

class Node {
  // constructor
  constructor(name, studentNumber, userName, pass) {
    this.name = name;
    this.studentNumber = studentNumber;
    this.userName = userName;
    this.pass = pass;
    this.next = null;
  }
}
// linkedlist class
class LinkedList {
  constructor() {
    this.head = null;
    this.size = 0;
  }

  add(name, studentNumber, userName, pass) {
    // creates a new node
    let node = new Node(name, studentNumber, userName, pass);
    let current;

    if (this.head == null)
      this.head = node;
    else {
      current = this.head;
      while (current.next) {
        current = current.next;
      }
      current.next = node;
    }
    this.size++;
  }

  insertAt(name, studentNumber, userName, pass, index) {
    if (index < 0 || index > this.size)
      return console.log("Please enter a valid index.");
    else {
      let node = new Node(name, studentNumber, userName, pass,);
      let curr, prev;

      curr = this.head;

      if (index == 0) {
        node.next = this.head;
        this.head = node;
      } else {
        curr = this.head;
        let it = 0;

        while (it < index) {
          it++;
          prev = curr;
          curr = curr.next;
        }

        node.next = curr;
        prev.next = node;
      }
      this.size++;
    }
  }

  removeFrom(index) {
    if (index < 0 || index >= this.size)
      return console.log("Please Enter a valid index");
    else {
      let curr, prev, it = 0;
      curr = this.head;
      prev = curr;

      // deleting first element
      if (index === 0) {
        this.head = curr.next;
      } else {
        // iterate over the list to the
        // position to remove an element
        while (it < index) {
          it++;
          prev = curr;
          curr = curr.next;
        }

        // remove the element
        prev.next = curr.next;
      }
      this.size--;

      // return the remove element
      return curr.element;
    }
  }

  removeElement(studentNumber) {
    let current = this.head;
    let prev = null;

    while (current != null) {
      if (current.studentNumber === studentNumber) {
        if (prev == null) {
          this.head = current.next;
        } else {
          prev.next = current.next;
        }
        this.size--;
        return current.element;
      }
      prev = current;
      current = current.next;
    }
    return -1;
  }

  indexOf(studentNumber) {
    let count = 0;
    let current = this.head;

    while (current != null) {
      if (current.studentNumber === studentNumber)
        return count;
      count++;
      current = current.next;
    }
    return -1;
  }

  // checks the list for empty
  isEmpty() {
    return this.size == 0;
  }

  size_of_list() {
    console.log(this.size);
  }

  printList() {
    let curr = this.head;
    let str = "";
    while (curr) {
      str += curr.name + " ";
      curr = curr.next;
    }
    console.log(str);
  }
}

