import './App.css';
import StudentCard from './StudentCards';
import React, { useState } from 'react';

const people = [
  {
    name: 'Mahdi',
    studentNum: 401243051,
    userName‌: "mm850",
    pass: 8596,
  },
  {
    name: 'Fam',
    studentNum: 402258961,
    userName‌: "mm350",
    pass: 1478,
  },
  {
    name: 'Hadi',
    studentNum: 456256888,
    userName‌: "mm350",
    pass: 1245,
  },
];

function App() {
  const [newPeople, setNewPeople] = useState(people);  // Correct state setter

  // Corrected listener function
  const StudentCardDeleteListener = (enteredId) => {
    console.log(enteredId);
    // Filter students to remove the one with the matched studentNum
    const updatedPeople = newPeople.filter(person => person.studentNum !== enteredId);
    setNewPeople(updatedPeople);  // Properly update the state
  }

  return (
    <div className='container'>
      {
        newPeople.map(student => (
          <StudentCard
            onChange={StudentCardDeleteListener}
            nameK={student.name}
            passK={student.pass}
            numberK={student.studentNum}
            id={student.studentNum}
            key={student.studentNum}
          />
        ))
      }
    </div>
  );
}

export default App;
