import './App.css';
import StudentCard from './StudentCards';
import React, { useState } from 'react';

class Node {
  // constructor
  constructor(element) {
    this.element = element;
    this.next = null
  }
}
class LinkedList {
  constructor() {
    this.head = null;
    this.size = 0;
  }

  add(element) {
    let node = new Node(element);

    let current;

    if (this.head == null)
      this.head = node;
    else {
      current = this.head;

      while (current.next) {
        current = current.next;
      }

      current.next = node;
    }
    this.size++;
  }
  // insertAt(element, location)
  insertAt(element, index) {
    if (index < 0 || index > this.size)
      return console.log("Please enter a valid index.");
    else {
      let node = new Node(element);
      let curr, prev;

      curr = this.head;

      if (index == 0) {
        node.next = this.head;
        this.head = node;
      } else {
        curr = this.head;
        let it = 0;

        while (it < index) {
          it++;
          prev = curr;
          curr = curr.next;
        }

        node.next = curr;
        prev.next = node;
      }
      this.size++;
    }
  }
  // removeFrom(location)
  // removeElement(element)

  // Helper Methods
  // isEmpty
  // size_Of_List
  // PrintList
}





const people = [
  {
    name: 'Mahdi',
    studentNum: "401243051",
    userName‌: "mm850",
    pass: 8596,
  },
  {
    name: 'Fam',
    studentNum: "402258961",
    userName‌: "mm350",
    pass: 1478,
  },
  {
    name: 'Hadi',
    studentNum: "456256888",
    userName‌: "mm350",
    pass: 1245,

  },
  {
    name: 'Hadi',
    studentNum: "4566888",
    userName‌: "mm350",
    pass: 1245,

  },
]

function App() {

  const [student, setStudent] = useState(people);
  const StudentCardDeleteListener = (enteredId) => {
    console.log(enteredId);
    let indexWhichShouldBeDeleted = -1
    for (let i = 0; i < student.length; i++) {
      if (student[i].studentNum == enteredId) {
        indexWhichShouldBeDeleted = i;
        break;
      }
    }
    const updatedStudent = [...student];
    updatedStudent.splice(indexWhichShouldBeDeleted, 1)
    setStudent(updatedStudent);
  }

  return (
    <div className='container'>
      {
        people.map(student => (
          <StudentCard
            onChange={StudentCardDeleteListener}
            nameK={student.name}
            passK={student.pass}
            numberK={student.studentNum}
            id={student.studentNum}
            key={student.studentNum}
          />
        ))
      }
    </div>
  );
}

export default App;
