import React, { useState } from "react";
import "./AddNewStudent.css"

const AddNewStudent = (props) => {


    const [Student_Name, setStudent_Name] = useState('');
    const [Student_Number, setStudent_Number] = useState();
    const [Student_User, setStudent_User] = useState("");
    const [Student_Pass, setStudent_Pass] = useState("");

    const [isNameOk, setIsNameOk] = useState(false);
    const [isUserOk, setIsUserOk] = useState(false);
    const [isPassOk, setIsPassOk] = useState(false);
    const [isNumberOk, setIsNumberOk] = useState(false);

    const [isInputOk, setIsInputOk] = useState({sN : false , });


    const Name_changeHandler = (event) => {
        setStudent_Name(event.target.value);
        if (event.target.value.trim().length) setIsNameOk(true);
        else setIsNameOk(false);
    }
    const SN_changeHandler = (event) => {
        setStudent_Number(event.target.value);
        // if (Student_Number === '') { setIsNumberOk(false); }
        // else setIsNumberOk(true);
    }
    const USER_changeHandler = (event) => {
        setStudent_User(event.target.value);
        // if (Student_User === '') setIsUserOk(false);
    }
    const PASS_changeHandler = (event) => {
        setStudent_Pass(event.target.value);
        // if (Student_Pass === '') setIsPassOk(false);
    }

    const SubmitHandler = (event) => {
        event.preventDefault();

        if (Student_Name.trim().length === 0) {
            setIsNameOk(false);
            return;
        } else {
            setIsNameOk(false);

            setStudent_Name('');
            setStudent_Number('');
            setStudent_User('');
            setStudent_Pass('');
            const temp = {
                sName: Student_Name,
                sNumber: Student_Number,
                sUser: Student_User,
                sPaas: Student_Pass,
            }
            props.OnAddNewStudentSubmit(temp);
        }
    }

    return (
        <div>
            <form onSubmit={SubmitHandler} className="add_student__form">
                <input
                    onChange={Name_changeHandler}
                    value={Student_Name}
                    className={`input_student ${isNameOk ? '' : 'invalid'}`}
                    type="text"
                    placeholder="Enter Name Of Student"
                />
                <input
                    onChange={SN_changeHandler}
                    value={Student_Number}
                    className={`input_student ${isNumberOk ? '' : 'invalid'}`}
                    type="number"
                    placeholder="Enter Student Number"
                />
                <input
                    onChange={USER_changeHandler}
                    value={Student_User}
                    className={`input_student ${isUserOk ? '' : 'invalid'}`}
                    type="text"
                    placeholder="Enter Student userName"
                />
                <input
                    onChange={PASS_changeHandler}
                    value={Student_Pass}
                    className={`input_student ${isPassOk ? '' : 'invalid'}`}
                    type="text"
                    placeholder="Enter Student pass"
                />
                <button className="input_student add_student_submit_button" onClick={SubmitHandler} type="submit">Add this Student</button>
            </form>
        </div>
    );
}

export default AddNewStudent;
